query:
{
  ecsTaskDefinitions(where: {AND: [
      {
        isLatestRevision: true
      },
      {
        containerSpecs_SOME: {
          privileged: true
        }
      }
    ]}) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
