query:
{
  iamPolicies(
    where: {
      policyName: "AWSSupportAccess"
      OR: [
        { attachedUserIDs_NOT: null }
        { attachedGroupIDs_NOT: null }
        { attachedRoleIDs_NOT: null }
      ]
    }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
