query:
{
  launchTemplateVersions(
    where: {
      OR: [
        {hasBlockDeviceMappings_NONE: null}
        {hasBlockDeviceMappings_SOME: {encrypted: false}}
      ]
    }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
