query:
{
  trails(
    where: {
      isMultiRegionTrail: true
      OR: [
        {
          status: { isLogging: true }
          eventSelectors_SOME: {
            includeManagementEvents: true
            readWriteType: "All"
          }
        }
        { isOrganizationTrail: true }
      ]
    }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
