query:
{
  rootUsers(
    where: {
      hasIAMUserCredentials: {
        OR: [{ accessKey1Active: true }, { accessKey2Active: true }]
      }
    }
  ) {
    connector {
      identifier
      accountID
      cloudAccountID
      cloudAccountName
      cloudProvider
      idFromProvider
      internalAssetType
      internalName
      internalRegion
      assetCategory
      contentHash
    }
  }
}
