query:
{
  storageAccounts(
    where: 
    {
  OR: [
    { AND: [{ networkRuleSetDefaultAction_CONTAINS: "Allow" }] }
    { AND: [{ networkRuleSetDefaultAction_CONTAINS: "Deny" }, {  networkRuleSetBypass_NOT_CONTAINS: "AzureServices" }] }
  ]
} 
  ) 
  {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
