query:
{
  sites(
    where: {
      siteConfig: {
        NOT: {
          OR: [
            { javaVersion: "" }
            { javaVersion_STARTS_WITH: "8" }
            { javaVersion_STARTS_WITH: "11" }
            { javaVersion_STARTS_WITH: "17" }
            { javaVersion_STARTS_WITH: "21" }
          ]
        }
      }
    }
  ) {
    ...AssetFragment
  }
}
