query:
{
  iamAccountSummaries(
    where: { hasIAMAccountSummaryItem_SOME: { key: "WDATP", value: 1 } }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
