query:
{
  iamCustomRoles(
    where: {
      isOwnedByIAMUser_NONE: null
      permissions_INCLUDES: "Microsoft.Authorization/locks"
    }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
