query:
{
  connectors(
    where: {
      cloudProvider: "entra"
      conditionalAccessPolicies_NONE: {
        conditions_SOME: {
          NOT: {
            excludeUsers: []
            OR: { includeUsers: [], includeGroups: [], includeRoles: [] }
          }
          includeApplications: ["All"]
          clientAppTypes: ["all"]
        }
        grantControls_SOME: { builtInControls: ["mfa"] }
      }
    }
  ) {
    ...AssetFragment
  }
}
