query:
{
  connectors(
    where: {
      conditionalAccessPolicies_NONE: {
        conditions_SOME: {
          includedUsers: ["All"]
          includedApplications: ["All"]
          clientAppTypes: ["all"]
          NOT: { includeLocations: [] }
        }
        grantControls_SOME: { builtInControls: ["block"] }
      }
    }
  ) {
    ...AssetFragment
  }
}
