query:
{
  buckets(where: {
    iamPolicyDocument_MATCHES: ".*domain:.*"
    hasIAMPolicy_SOME: {
      hasBucketIAMPolicyBinding_SOME: {
        OR: [
          { members_INCLUDES: "allAuthenticatedUsers" },
          { members_INCLUDES: "allUsers" }
        ]
      }
    }
  }) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
  }
}
