query:
{
  pods(
    where: {
      OR: [
        {
          containers_SOME: {
            env_SOME: {
              AND: { isValueFromSet: true, isSecretKeySelectorSet: true }
            }
          }
        },
        {
          secretVolumes_NOT: []
        }
      ]
    }
  ) {
    ...AssetFragment
  }
}
