query:
{
  roles(
    where: {
      rules_SOME: {
        AND: [
          {
            OR: [
              { verbs_INCLUDES: "get" }
              { verbs_INCLUDES: "list" }
              { verbs_INCLUDES: "watch" }
              { verbs_INCLUDES: "*" }
            ]
          }
          {
            OR: [{ resources_INCLUDES: "secrets" }, { resources_INCLUDES: "*" }]
          }
          { OR: [{ apiGroup_INCLUDES: "" }, { apiGroup_INCLUDES: "*" }] }
        ]
      }
    }
  ) {
    bindings {
      ...AssetFragment
    }
  }
}
