query:
{
  vms(
    where: {
      networkInterfaces_SOME: { hasNetworkInterfaceAccessConfig_NOT: null }
      firewalls_SOME: {
        rules_SOME: {
          direction: "Inbound"
          sourceFromPort_LTE: 22
          sourceToPort_GTE: 22
          OR: [
            { sources_INCLUDES: "cidr:0.0.0.0/0" }
            { sources_INCLUDES: "cidr:::/0" }
          ]
        }
      }
    }
  ) {
    diskAttachments {
      disk {
        identifier
        accountID
        cloudAccountID
        cloudAccountName
        cloudProvider
        idFromProvider
        internalAssetType
        internalName
        internalRegion
        assetCategory
        contentHash
      }
    }
  }
}
