query:
{
  blobContainers(
    where: {
      publicAccessBlocked: false
      publicAccess_IN: ["Blob", "Container"]
    }
  ) {
    identifier
    accountID
    cloudAccountID
    cloudAccountName
    cloudProvider
    idFromProvider
    internalAssetType
    internalName
    internalRegion
    assetCategory
    contentHash
  }
}
