query:
{
  pods(
    where: {
      OR: [
        {
          AND: [
            { isAutomountServiceAccountTokenSet: true }
            { automountServiceAccountToken: true }
          ]
        }
        {
          AND: [
            { isAutomountServiceAccountTokenSet: false }
            {
              serviceAccount: {
                isAutomountServiceAccountTokenSet: true
                automountServiceAccountToken: true
              }
            }
          ]
        }
        {
          AND: [
            { isAutomountServiceAccountTokenSet: false }
            { serviceAccount: { isAutomountServiceAccountTokenSet: false } }
          ]
        }
      ]
    }
  ) {
    ...AssetFragment
  }
}
