query:
{
  sqlServers(
    where: {
      firewallRules_SOME: {
        OR: [
          { name_MATCHES: "(?i)allowallwindowsazureips" }
          { name_MATCHES: "(?i)allowallazureips" }
          { AND: [{ startIpAddress: "0.0.0.0" }, { endIpAddress: "0.0.0.0" }] }
        ]
      }
    }
  ) {...AssetFragment}
}
